    private void ButtonTiendaPasarActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_ButtonTiendaPasarActionPerformed
        // TODO add your handling code here:
        int filaSeleccionada = TablaAlmacen.getSelectedRow();
        Tienda tienda = new Tienda();
        tienda.setVisible(true);
        DefaultTableModel model = (DefaultTableModel) tienda.TablaTienda.getModel();
        model.addRow(new Object[]{TablaAlmacen.getValueAt(filaSeleccionada, 0), TablaAlmacen.getValueAt(filaSeleccionada, 1), TablaAlmacen.getValueAt(filaSeleccionada, 2), TablaAlmacen.getValueAt(filaSeleccionada, 3),TablaAlmacen.getValueAt(filaSeleccionada, 4),TablaAlmacen.getValueAt(filaSeleccionada, 5),});
    }//GEN-LAST:event_ButtonTiendaPasarActionPerformed

